mapper.select:
select * from ( select row_number() over (order by allowd_id) as rn , allowd_id, allowd_name from (select distinct allowd_id, allowd_name from allow_departments) as distinct_allowd
where allowd_name like concat('%',#{searchWord},'%')
) as sub
where rn between #{startRow} and #{endRow}

mapper.update:
update allow_departments
set allowd_name=#{allowdName}
, department_id=#{departmentId}
where allowd_id=#{allowdId}
